/**
 * Customize default theme styling by overriding CSS variables:
 * https://github.com/vuejs/vitepress/blob/main/src/client/theme-default/styles/vars.css
 */

/**
 * Colors
 *
 * Each colors have exact same color scale system with 3 levels of solid
 * colors with different brightness, and 1 soft color.
 * 
 * - `XXX-1`: The most solid color used mainly for colored text. It must
 *   satisfy the contrast ratio against when used on top of `XXX-soft`.
 *
 * - `XXX-2`: The color used mainly for hover state of the button.
 *
 * - `XXX-3`: The color for solid background, such as bg color of the button.
 *   It must satisfy the contrast ratio with pure white (#ffffff) text on
 *   top of it.
 *
 * - `XXX-soft`: The color used for subtle background such as custom container
 *   or badges. It must satisfy the contrast ratio when putting `XXX-1` colors
 *   on top of it.
 *
 *   The soft color must be semi transparent alpha channel. This is crucial
 *   because it allows adding multiple "soft" colors on top of each other
 *   to create a accent, such as when having inline code block inside
 *   custom containers.
 *
 * - `default`: The color used purely for subtle indication without any
 *   special meanings attached to it such as bg color for menu hover state.
 *
 * - `brand`: Used for primary brand colors, such as link text, button with
 *   brand theme, etc.
 *
 * - `tip`: Used to indicate useful information. The default theme uses the
 *   brand color for this by default.
 *
 * - `warning`: Used to indicate warning to the users. Used in custom
 *   container, badges, etc.
 *
 * - `danger`: Used to show error, or dangerous message to the users. Used
 *   in custom container, badges, etc.
 * -------------------------------------------------------------------------- */

:root {
    --vp-c-default-1: var(--vp-c-gray-1);
    --vp-c-default-2: var(--vp-c-gray-2);
    --vp-c-default-3: var(--vp-c-gray-3);
    --vp-c-default-soft: var(--vp-c-gray-soft);

    --vp-c-brand-1: var(--vp-c-indigo-1);
    --vp-c-brand-2: var(--vp-c-indigo-2);
    --vp-c-brand-3: var(--vp-c-indigo-3);
    --vp-c-brand-soft: var(--vp-c-indigo-soft);

    --vp-c-tip-1: var(--vp-c-brand-1);
    --vp-c-tip-2: var(--vp-c-brand-2);
    --vp-c-tip-3: var(--vp-c-brand-3);
    --vp-c-tip-soft: var(--vp-c-brand-soft);

    --vp-c-warning-1: var(--vp-c-yellow-1);
    --vp-c-warning-2: var(--vp-c-yellow-2);
    --vp-c-warning-3: var(--vp-c-yellow-3);
    --vp-c-warning-soft: var(--vp-c-yellow-soft);

    --vp-c-danger-1: var(--vp-c-red-1);
    --vp-c-danger-2: var(--vp-c-red-2);
    --vp-c-danger-3: var(--vp-c-red-3);
    --vp-c-danger-soft: var(--vp-c-red-soft);
}

/**
 * Component: Button
 * -------------------------------------------------------------------------- */

:root {
    --vp-button-brand-border: transparent;
    --vp-button-brand-text: var(--vp-c-white);
    --vp-button-brand-bg: var(--vp-c-brand-3);
    --vp-button-brand-hover-border: transparent;
    --vp-button-brand-hover-text: var(--vp-c-white);
    --vp-button-brand-hover-bg: var(--vp-c-brand-2);
    --vp-button-brand-active-border: transparent;
    --vp-button-brand-active-text: var(--vp-c-white);
    --vp-button-brand-active-bg: var(--vp-c-brand-1);
}

/**
 * Component: Home
 * -------------------------------------------------------------------------- */

:root {
    --vp-home-hero-name-color: transparent;
    --vp-home-hero-name-background: -webkit-linear-gradient(120deg, #bd34fe 30%, #41d1ff);

    --vp-home-hero-image-background-image: linear-gradient(-45deg, #bd34fe 50%, #47caff 50%);
    --vp-home-hero-image-filter: blur(44px);
}

@media (min-width: 640px) {
    :root {
        --vp-home-hero-image-filter: blur(56px);
    }
}

@media (min-width: 960px) {
    :root {
        --vp-home-hero-image-filter: blur(68px);
    }
}

/**
 * Component: Custom Block
 * -------------------------------------------------------------------------- */

:root {
    --vp-custom-block-tip-border: transparent;
    --vp-custom-block-tip-text: var(--vp-c-text-1);
    --vp-custom-block-tip-bg: var(--vp-c-brand-soft);
    --vp-custom-block-tip-code-bg: var(--vp-c-brand-soft);
}

/**
 * Component: Algolia
 * -------------------------------------------------------------------------- */

.DocSearch {
    --docsearch-primary-color: var(--vp-c-brand-1) !important;
}

.VPHome {
    margin-bottom: 0 !important;
}

.vp-doc .simple-table table {
    margin: 0;
}

.VPDoc > .container > .content {
    padding: 0;
}

.VPDocOutlineItem li {
    list-style: none;
}
.VPDoc.has-aside .content-container {
    max-width: none !important;
}

import path from 'node:path'
import { fileURLToPath } from 'node:url'
import VueI18nPlugin from '@intlify/unplugin-vue-i18n/vite'

import vueJsx from '@vitejs/plugin-vue-jsx'
import { ElementPlusResolver } from 'unplugin-vue-components/resolvers'
import Components from 'unplugin-vue-components/vite'
import AutoImport from 'unplugin-auto-import/vite'

import { defineConfig } from 'vitepress'
import { MermaidMarkdown, MermaidPlugin } from 'vitepress-plugin-mermaid'
import { vitepressDemoPlugin } from 'vitepress-demo-plugin'
import UnoCSS from 'unocss/vite'

import { en } from './en'
import { repoUrl } from './shared'
import { injectPreAndNextLink } from './utils'
import { zh } from './zh'

const utilsUrl = path.resolve(import.meta.dirname, '../../src/utils/index.ts')
// https://vitepress.dev/reference/site-config
export default defineConfig((envObj) => {
    const { mode, command } = envObj
    return {
        // srcDir: 'docs',
        lang: 'zh-cn',
        head: [['link', { rel: 'icon', type: 'image/png', href: '/vite.png' }]],
        themeConfig: {
            logo: { src: '/vite.png', width: 24, height: 24 },
            // https://vitepress.dev/reference/default-theme-config
            socialLinks: [{ icon: 'github', link: repoUrl }],
        },
        rewrites: {
            'zh-cn/:rest*': ':rest*',
        },
        locales: {
            root: { label: '简体中文', ...zh },
            'en-us': { label: 'English', ...en },
        },
        markdown: {
            lineNumbers: true,
            config(md) {
                md.use(MermaidMarkdown)
                md.use(vitepressDemoPlugin, {
                    demoDir: path.resolve(import.meta.dirname, '../..'),
                })
            },
        },
        transformPageData(pageData, ctx) {
            return injectPreAndNextLink(pageData, ctx)
        },
        vite: {
            resolve: {
                alias: {
                    root: fileURLToPath(new URL('../../src', import.meta.url)),
                },
            },
            plugins: [
                vueJsx(),
                UnoCSS(),
                MermaidPlugin(),
                AutoImport({
                    resolvers: [ElementPlusResolver()],
                    include: [
                        /\.[tj]sx?$/, // .ts, .tsx, .js, .jsx
                        /\.vue$/,
                        /\.vue\?vue/, // .vue
                    ],
                    imports: [
                        'vue',
                        'pinia',
                        'vue-router',
                        {
                            'root/utils/index.tsx': [['*', 'utils']],
                            'vue-i18n': ['useI18n'],
                            dayjs: [['default', 'dayjs']],
                        },
                    ],
                    dts: 'typings/auto-imports.d.ts',
                    vueTemplate: true,
                }),
                Components({
                    resolvers: [ElementPlusResolver()],
                    include: [
                        /\.[tj]sx?$/, // .ts, .tsx, .js, .jsx
                        /\.vue$/,
                        /\.vue\?vue/, // .vue
                    ],
                    dts: 'typings/components.d.ts',
                }),
                VueI18nPlugin({
                    ssr: true,
                    defaultSFCLang: 'yaml',
                    allowDynamic: true,
                    // locale messages resource pre-compile option
                }),
            ],
            optimizeDeps: {
                include: ['mermaid'],
            },
            ssr: {
                noExternal: ['mermaid'],
            },
            css: {
                // css预处理器
                preprocessorOptions: {
                    scss: {
                        additionalData: [
                            `$env: "${mode}";`, // 注入SCSS变量
                            '@import "root/assets/styles/mixin.scss";',
                        ].join('\n'),
                    },
                },
            },
            server: {
                proxy: {
                    '/pdfjs': {
                        target: 'http://localhost:8088',
                        changeOrigin: true,
                    },
                    '/mock': {
                        target: 'http://localhost:8088',
                        changeOrigin: true,
                    },
                },
            },
        },
    }
})

import { defineConfig } from 'vitepress'

import { currentVersion, editPattern } from './shared'
import { createNav, createSideBar } from './utils'

export const en = defineConfig({
  lang: 'en-US',
  title: 'di-ui',
  description: 'Biz UI Components For Vue 3',
  base: '/en-us/',
  themeConfig: {
    nav: createNav('en-us', [
      ['Guild', 'guide'],
      ['Components', 'components'],
    ]).concat({
      text: currentVersion,
      link: '',
    }),
    get sidebar() {
      return createSideBar('en-us', [
        ['Guild', 'guide'],
        ['Components', 'components'],
      ])
    },
    editLink: {
      pattern: editPattern,
