                        } else {
                            child.status = 0
                        }
                    })
                }
            }
            await jsonWrite(jsonPath, data)
            simpleSend(req, res, {
                code: '200',
                message: '成功',
            })
        },
    },
    {
        url: url + `/forms/template/id`,
        method: 'get',
        rawResponse: async (req, res) => {
            const query = qs.parse(req._parsedUrl.query)
            const data = await jsonRead(jsonPath)
            let filePath
            for (let i = 0; i < data.length; i++) {
                const item = data[i]
                if (item.id == query.id) {
                    filePath = item.filePath
                }
            }
            const template = await jsonRead('/mock/database/forms/template/' + filePath)

            simpleSend(req, res, {
                code: '200',
                message: '成功',
                data: template,
            })
        },
    },
    {
        url: url + `/forms/template/history`,
        method: 'get',
        rawResponse: async (req, res) => {
            const query = qs.parse(req._parsedUrl.query)

            const data = await jsonRead(jsonPath)
            let result
            for (let i = 0; i < data.length; i++) {
                const item = data[i]
                if (item.id == query.id) {
                    result = item
                }
            }

            simpleSend(req, res, {
                code: '200',
