import { utils as xUtils, writeFile } from 'xlsx-js-style'

export default async function (table, excelFileName, excelColInfo, openSelect, showExportField, field) {
    const fileName = (excelFileName || dayjs().format('YYYY-MM-DD')) + '.xlsx'
    const tableHeader = table.$el.querySelector('.el-table__header').cloneNode(true)
    const tableBody = table.$el.querySelector('.el-table__body').cloneNode(true)
    let rows = tableBody.querySelectorAll('tbody tr')

    //处理勾选行导出
    if (openSelect) {
        // 获取表格的所有行
        // 遍历每一行
        rows.forEach((row) => {
            // 检查是否包含 is-checked 类
            const isChecked = row.querySelector('.is-checked')
            if (!isChecked) {
                // 执行后，会在tableBody中移除该行
                row.remove()
            }
        })
        rows = tableBody.querySelectorAll('tbody tr')
        if (rows.length == 0) {
            return
        }
    }
    //处理勾选列导出
    if (showExportField) {
        const ths = tableHeader.querySelectorAll('tr th') // 获取所有 th

        // 找到文本内容与数组一致的 th
        const cellIndexArr = Array.from(ths)
            .filter((th) => field.includes(th.textContent.trim()))
            .map((th) => th.cellIndex)

        ths.forEach((th) => {
            if (!field.includes(th.textContent.trim())) {
                th.remove()
            }
        })

        rows.forEach((row) => {
            const tds = row.querySelectorAll('td')
            tds.forEach((td, index) => {
                console.log(td.cellIndex)
                if (!cellIndexArr.includes(index)) {
                    td.remove()
                }
            })
        })
    }
    // 准备转excel
    const ws = xUtils.table_to_sheet(tableHeader)
    Object.keys(ws).forEach((key) => {
        if (!key.includes('!')) {
            ws[key].s = { font: { bold: true } }
        }
    })
    xUtils.sheet_add_dom(ws, tableBody, { origin: -1 })

    const boldRow = []

    Object.keys(ws).forEach((key) => {
        if (!key.includes('!')) {
            if (ws[key].s) {
                ws[key].s = {
                    font: { name: '宋体', sz: 16, bold: true },
                    alignment: {
                        horizontal: 'center',
                        vertical: 'center',
                    },
                }
            } else {
                ws[key].s = {
                    font: { name: '宋体', sz: 14 },
                    alignment: {
                        horizontal: 'center',
                        vertical: 'center',
                    },
                }
                const row = key.slice(1)
                if (ws[key].v === '小计' || ws[key].v === '总计') {
                    boldRow.push(row)
                    ws[key].s.font.bold = true
                } else if (boldRow.includes(row)) {
                    ws[key].s.font.bold = true
                }
            }
        }
    })
    ws['!cols'] = excelColInfo
    const wb = xUtils.book_new()
    xUtils.book_append_sheet(wb, ws)
    writeFile(wb, fileName, { cellStyles: true })
}

import { defaults, isNil, cloneDeep } from 'lodash'

export function initCol(columns) {
    const queryList = ref([])
    const queryFormRules = ref({})
    const queryFormModel = ref({})
    const mergeCol = ref([]) //记录表格需要处理单元格合并的列
    const subTotalKeys = ref([]) //记录表格需要处理统计列
    const subAndGrandOptions = ref([]) //记录小计总计

    const { list, rules, model, merges, subs, totalOptions, sort } = columnRecursion(columns)

    // sort数组按照其元素querySort属性排序
    sort.sort((a, b) => a.formItem.querySort - b.formItem.querySort)
    //处理有特定查询区域排序的情况
    sort.forEach((item) => {
        list.splice(item.formItem.querySort, 0, item)
    })
    queryList.value = cloneDeep(list)
    queryFormRules.value = rules
    queryFormModel.value = model
    mergeCol.value = merges
    subTotalKeys.value = subs
    subAndGrandOptions.value = totalOptions

    return {
        queryList,
        queryFormRules,
        queryFormModel,
        mergeCol,
        subTotalKeys,
        subAndGrandOptions,
    }
}

/**
 * Description placeholder
 *
 * @export
 * @param {*} columns  表格列配置
 * @returns {{ list: [];  rules: {}; model: {}; merges: []; subs: []; }} {list：查询区配置数据；rules：查询区表单规则；model：查询区表单数据对象；merges:需执行行合并列；subs：需计算小计列}
 */
function columnRecursion(columns) {
    const list = []
    const sort = []
    const rules = {}
    const model = {}
    const merges = []
    const subs = []
    const totalOptions = {}

    columns.forEach((item) => {
        //<-----处理表格查询区域相关
        if (item.formItem && !item.queryHidden) {
            if (item.formItem.querySort || item.formItem.querySort === 0) {
                sort.push(item)
            } else {
                list.push(item)
            }
            const key = item.dataIndex
            if (item.formItem.queryRules) {
                rules[key] = item.formItem.queryRules
            }

            model[key] = item.formItem.value
        }
        //处理表格查询区域相关----->

        //收集需执行行合并的列
        if (item.merge) {
            merges.push(item.dataIndex)
        }

        //<-----处理小计总计相关
        if (item.grandTotalTitle) {
            totalOptions.grandTotalTitle = item.grandTotalTitle
            totalOptions.grandTotalKey = item.dataIndex
        }
        if (item.subTotalTitle) {
            totalOptions.subTotalTitle = item.subTotalTitle
            totalOptions.subTotalKey = item.dataIndex
        } else if (item.subTotal) {
            subs.push(item.dataIndex)
        }
        //处理小计总计相关----->

        if (item.children) {
            const childRes = columnRecursion(item.children)
            sort.push(...childRes.sort)
            list.push(...childRes.list)
            Object.assign(rules, childRes.rules)
            Object.assign(model, childRes.model)
            //defaults方法中，当前一个对象有值是，后续对象的值忽略
            Object.assign(totalOptions, defaults(totalOptions, childRes.totalOptions))
            merges.push(...childRes.merges)
            subs.push(...childRes.subs)
        }
    })

    return { list, rules, model, merges, subs, totalOptions, sort }
}

type SubAndGrandOptionsType = {
    subTotalKey: string
    grandTotalKey: string
    subTotalTitle: string
    subTotalKey: string
}
/**
 * 添加小计行与总计行
 *
 * @export
 * @param {*} list 表格数据
 * @param {*} subAndGrandOptions subAndGrandOptions
 * @returns {{}}
 */
export function addSubtotals(list: [], subAndGrandOptions: SubAndGrandOptionsType, subKeys: []) {
    if (!list.length) return []
    const { grandTotalKey, subTotalKey } = subAndGrandOptions
    const groupKey = grandTotalKey ?? subTotalKey
    const result = []
    let group = []
    let lastGroupVal = list[0][groupKey]

    if (subTotalKey) {
        list.forEach((item, idx) => {
            if (item[groupKey] !== lastGroupVal) {
                result.push(...group, createSubtotalRow(group, subAndGrandOptions, subKeys))
                group = []
                lastGroupVal = item[groupKey]
            }
            group.push(item)

            if (idx === list.length - 1) {
                // 最后一条，则添加小计行数据
                result.push(...group, createSubtotalRow(group, subAndGrandOptions, subKeys))
            }
        })
    } else {
        result.push(...list)
    }

    // 则添加总计行数据
    if (grandTotalKey) {
        result.push(createGrandTotalRow(list, subAndGrandOptions, subKeys))
    }
    return result

    function createSubtotalRow(group, subAndGrandOptions, subKeys) {
        const { subTotalKey, grandTotalKey, subTotalTitle } = subAndGrandOptions
        const result = {
            isSubtotal: true,
            [grandTotalKey]: group[0][grandTotalKey],
            [subTotalKey]: subTotalTitle || '小计',
        }
        subKeys.forEach((key) => {
            result[key] = group.reduce((sum, row) => sum + Number(row[key]), 0)
        })
        return result
    }
    function createGrandTotalRow(list, subAndGrandOptions, subKeys) {
        const { grandTotalKey, grandTotalTitle } = subAndGrandOptions
        const result = { isTotal: true, [grandTotalKey]: grandTotalTitle || '总计' }
        subKeys.forEach((key) => {
            result[key] = list.reduce((sum, row) => sum + Number(row[key]), 0)
        })
        return result
    }
}

/**
 * 设置表格列合并
 *
 * @export
 * @param {*} list 表格数据
 * @param {*} otherSpanMethod 外部传入的合并设置
 * @param {*} mergeCol 需执行行合并的列
 * @param {*} subTotalKeys 小计列key集合
 */

export function setSpanMethod(list: [], otherSpanMethod: Function, mergeCol: [], subTotalKeys: string[]) {
    return ({ row, column, rowIndex, columnIndex }) => {
        const dataIndex = column.property

        let result
        if (typeof otherSpanMethod === 'function') {
            result = otherSpanMethod({ row, column, rowIndex, columnIndex })
        }

        if (result) {
            return result
        }

        if (mergeCol.includes(dataIndex)) {
            if (isNil(row['$merge_' + dataIndex]) || row['$merge_' + dataIndex] === rowIndex) {
                row['$merge_' + dataIndex] = rowIndex
                let rowspan = 1
                for (let i = rowIndex + 1; i < list.length; i++) {
                    if (isSameParent(list[i], row, dataIndex)) {
                        list[i]['$merge_' + dataIndex] = rowIndex
                        rowspan++
                    } else {
                        break
                    }
                }

                return { rowspan, colspan: 1 }
            } else {
                return { rowspan: 0, colspan: 0 }
            }
        }

        return { rowspan: 1, colspan: 1 }
    }

    function isSameParent(listItem, row, dataIndex) {
        for (let i = 0; i < mergeCol.length; i++) {
            const key = mergeCol[i]
            if (listItem[key] !== row[key]) {
                return false
            }

            if (mergeCol[i] === dataIndex) {
                break
            }
        }
        return true
    }
}

import { uniqWith, isEqual } from 'lodash'

function columnGroup({ tableData, columns }) {
    const result = []
    for (const column of columns) {
        if (!column.tableHidden) {
            result.push(
                <el-table-column
                    prop={column.dataIndex}
                    label={$t(column.tableTitle ?? column.title)}
                    align={column.align ?? 'center'}
                    width={column.width}
                    min-width={column.minWidth}
                    fixed={column.fixed}
                    type={column.columnType || 'default'}
                    class-name={column.className}
                    show-overflow-tooltip={column.overflow}
                    filters={column.filter ? filtersHandler(tableData, column) : null}
                    filter-method={column.filter ? filterMethodHandler : null}
                    {...column.columnAttr}>
                    {{
                        default({ row, $index }) {
                            if (column.children) {
                                return columnGroup({ columns: column.children })
                            } else if (typeof column.customRender === 'function') {
                                return column.customRender(row[column.dataIndex], row, $index)
                            }
                        },
                    }}
                </el-table-column>,
            )
        }
    }

    return result
}

export default columnGroup

function filtersHandler(tableData, column) {
    return uniqWith(
        tableData.map((row) => {
            return {
                text: row[column.dataIndex],
                value: row[column.dataIndex],
            }
        }),
        isEqual,
    )
}
function filterMethodHandler(value, row, column) {
    const property = column['property']
    return row[property] == value
}

import { Graph, Shape } from '@antv/x6'
import { Transform } from '@antv/x6-plugin-transform'
import { Selection } from '@antv/x6-plugin-selection'
import { Snapline } from '@antv/x6-plugin-snapline'
import { Keyboard } from '@antv/x6-plugin-keyboard'
import { Clipboard } from '@antv/x6-plugin-clipboard'
import { History } from '@antv/x6-plugin-history'

export default function initGraph(id: string) {
    const graph = new Graph({
        container: document.getElementById(id),
        grid: {
            visible: true,
            type: 'dot',
            args: {
                color: '#a0a0a0', // 网点颜色
                thickness: 1, // 网点大小
            },
        },
        panning: true,
        autoResize: true,
        mousewheel: {
            enabled: true,
            zoomAtMousePosition: true,
            modifiers: 'ctrl',
            minScale: 0.5,
            maxScale: 3,
        },
        connecting: {
            router: 'manhattan',
            connector: {
                name: 'rounded',
                args: {
                    radius: 8,
                },
            },
            anchor: 'center',
            connectionPoint: 'anchor',
            allowBlank: false,
            snap: {
                radius: 20,
            },
            createEdge() {
                return new Shape.Edge({
                    attrs: {
                        line: {
                            stroke: '#A2B1C3',
                            strokeWidth: 2,
                            targetMarker: {
                                name: 'block',
                                width: 12,
                                height: 8,
                            },
                        },
                    },
                    zIndex: 0,
                })
            },
            validateConnection({ targetMagnet }) {
                return !!targetMagnet
            },
        },
        highlighting: {
            magnetAdsorbed: {
                name: 'stroke',
                args: {
                    attrs: {
                        fill: '#5F95FF',
                        stroke: '#5F95FF',
                    },
                },
            },
        },
    })

    // #region 使用插件
    graph
        .use(
            new Transform({
                resizing: true,
                rotating: true,
            }),
        )
        .use(
            new Selection({
                rubberband: true,
                showNodeSelectionBox: true,
            }),
        )
        .use(new Snapline())
        .use(new Keyboard())
        .use(new Clipboard())
        .use(new History())

    // #region 快捷键与事件
    graph.bindKey(['meta+c', 'ctrl+c'], () => {
        const cells = graph.getSelectedCells()
        if (cells.length) {
            graph.copy(cells)
        }
        return false
    })
    graph.bindKey(['meta+x', 'ctrl+x'], () => {
        const cells = graph.getSelectedCells()
        if (cells.length) {
            graph.cut(cells)
        }
        return false
    })
    graph.bindKey(['meta+v', 'ctrl+v'], () => {
        if (!graph.isClipboardEmpty()) {
            const cells = graph.paste({ offset: 32 })
            graph.cleanSelection()
            graph.select(cells)
        }
        return false
    })

    // undo redo
    graph.bindKey(['meta+z', 'ctrl+z'], () => {
        if (graph.canUndo()) {
            graph.undo()
        }
        return false
    })
    graph.bindKey(['meta+shift+z', 'ctrl+shift+z'], () => {
        if (graph.canRedo()) {
            graph.redo()
        }
        return false
    })

    // select all
    graph.bindKey(['meta+a', 'ctrl+a'], () => {
        const nodes = graph.getNodes()
        if (nodes) {
            graph.select(nodes)
        }
    })

    // delete
    graph.bindKey('backspace', () => {
        const cells = graph.getSelectedCells()
        if (cells.length) {
            graph.removeCells(cells)
        }
    })

    // zoom
    graph.bindKey(['ctrl+1', 'meta+1'], () => {
        const zoom = graph.zoom()
        if (zoom < 1.5) {
            graph.zoom(0.1)
        }
    })
    graph.bindKey(['ctrl+2', 'meta+2'], () => {
        const zoom = graph.zoom()
        if (zoom > 0.5) {
            graph.zoom(-0.1)
        }
    })

    // 控制连接桩显示/隐藏
    function showPorts(ports, show) {
        for (let i = 0, len = ports.length; i < len; i += 1) {
            ports[i].style.visibility = show ? 'visible' : 'hidden'
        }
    }

    graph.on('node:mouseenter', (e) => {
        const container = document.getElementById('graph-container')
        const ports = container.querySelectorAll('.x6-port-body')
        // 不需要展示控制桩的节点
        const notControlPoint = []
        if (notControlPoint.includes(e.cell.shape)) {
            showPorts(ports, false)
            return
        }
        showPorts(ports, true)
    })

    graph.on('node:mouseleave', () => {
        const container = document.getElementById('graph-container')
        const ports = container.querySelectorAll('.x6-port-body')
        showPorts(ports, false)
    })

    // 边hover事件
    graph.on('edge:mouseenter', ({ edge }) => {
        const style = {
            line: {
                stroke: '#5F95FF',
                strokeWidth: 2,
            },
        }
        edge.attr(style)
    })
    graph.on('edge:mouseleave', ({ edge }) => {
        const style = {
            line: {
                stroke: '#A2B1C3',
                strokeWidth: 2,
            },
        }
        edge.attr(style)
    })

    return graph
}

export function validGraph(graphData) {
    if (graphData.cells.length === 0) {
        ElMessage.warning('当前流程未设置！')
        return false
    }
    const nodes = graphData.cells.filter((m) => m.shape != 'edge')
    const edges = graphData.cells.filter((m) => m.shape === 'edge')
    // 开始节点的数量已经结束节点的数量
    const beginNode = nodes.filter((n) => n.shape === 'custom-start')
    const endNode = nodes.filter((n) => n.shape === 'custom-end')
    // 开始节点作为边的source节点数量只能有一个
    // 审批节点的数量
    const taskNode = nodes.filter((n) => n.shape === 'custom-task')
    // 边的来源和目标相同的数量
    const errorEdge = edges.filter((e) => e.source.cell === e.target.cell)
    // 判断有无开始节点以及结束节点 且只能有一个开始节点以及结束节点
    // 边的来源和目标不能相同
    // 判断有无审批节点,必须至少有一个审批节点
    if (beginNode.length !== 1 || endNode.length !== 1 || taskNode.length === 0 || errorEdge.length > 0) {
        ElMessage.warning('当前流程设置有误，请检查！')
        return false
    }

    // 开始节点只能有输出边且必须有，结束节点只能有输入边且必须有
    // 开始节点的边不能直接指向结束节点
    // 审批节点只能有一条输出边
    // 开始节点的边只能有一条
    const beginEdges = edges.filter((m) => m.source.cell === beginNode[0].id)
    const beginTargetEdges = edges.filter((m) => m.target.cell === beginNode[0].id)
    const endEdges = edges.filter((m) => m.source.cell === endNode[0].id)
    const beginToEndEdges = edges.filter((m) => m.source.cell === beginNode[0].id && m.target.cell === endNode[0].id)

    // 审批节点只能 有一条输出边
    const nodeEdges = []
    let nodeParamsIsNull = false
    for (const node of taskNode) {
        const nodeEdge = edges.filter((edge) => edge.source.cell == node.id)
        if (nodeEdge.length > 1) {
            nodeEdges.push(true)
        } else {
            nodeEdges.push(false)
        }
        // if (!node.attrs.hasOwnProperty('customText')) {
        //     nodeParamsIsNull = true
        // }
    }
    const hasExistMultipleEdge = nodeEdges.filter((m) => m)
    if (
        beginEdges.length !== 1 ||
        beginTargetEdges.length !== 0 ||
        endEdges.length !== 0 ||
        beginToEndEdges.length !== 0 ||
        hasExistMultipleEdge.length > 0 ||
        nodeParamsIsNull
    ) {
        ElMessage.warning('当前流程设置有误，请检查！')
        return false
    }

    // 分支条件只有一个输入边，且边要有条件设置
    return true
}
export function toBpmn(graphData) {
    let bpmn = `<?xml version="1.0" encoding="UTF-8"?>
        <bpmn:definitions xmlns:bpmn="http://www.omg.org/spec/BPMN/20100524/MODEL" xmlns:bpmndi="http://www.omg.org/spec/BPMN/20100524/DI" xmlns:dc="http://www.omg.org/spec/DD/20100524/DC" xmlns:camunda="http://camunda.org/schema/1.0/bpmn" xmlns:di="http://www.omg.org/spec/DD/20100524/DI" xmlns:xsi="http://www.w3.org/2001/XMLSchema-instance" xmlns:modeler="http://camunda.org/schema/modeler/1.0" id="Definitions_0kosu2f" targetNamespace="http://bpmn.io/schema/bpmn" exporter="Camunda Modeler" exporterVersion="5.19.0" modeler:executionPlatform="Camunda Platform" modeler:executionPlatformVersion="7.20.0">
        <bpmn:process id="Process_file_approve_1" name="文件审批流程-0418-1" processType="None" isClosed="false" isExecutable="true" camunda:historyTimeToLive="180">`
    const nodes = graphData.cells.filter((m) => m.shape != 'edge')
    const edges = graphData.cells.filter((m) => m.shape === 'edge')
    for (const node of nodes) {
        if (node.shape === 'custom-start' || node.shape === 'custom-end') {
            // 开始或者结束
            if (node.attrs.label.text === '开始') {
                bpmn += `<bpmn:startEvent id="n${node.id}">
                <bpmn:outgoing>s${getEdgeId(node.id)}</bpmn:outgoing>
                </bpmn:startEvent>`
            } else {
                const inList = getInputEdgeTargets(node.id)
                let str = ''
                for (const inLine of inList) {
                    str += `<bpmn:incoming>s${inLine.id}</bpmn:incoming>`
                }
                bpmn += `<bpmn:endEvent id="n${node.id}">
            ${str}
            </bpmn:endEvent>`
            }
        } else if (node.shape === 'custom-task') {
            console.log(node.attrs?.customText?.value)
            if (node.attrs?.customText?.name) {
                const list = JSON.parse(node.attrs.customText.value).map((m) => {
                    return {
                        roleVariety: m.roleVariety,
                        roleList: m.roleList,
                    }
                })
                bpmn += `<bpmn:userTask id="n${node.id}">
                <bpmn:extensionElements>
                <camunda:inputOutput>
                    <camunda:inputParameter name="${node.attrs.customText.name}">
                        ${JSON.stringify(list)}
                    </camunda:inputParameter>
                </camunda:inputOutput>
                </bpmn:extensionElements>
                <bpmn:incoming>s${getEdgeTargetId(node.id)}</bpmn:incoming>
                <bpmn:outgoing>s${getEdgeId(node.id)}</bpmn:outgoing>
            </bpmn:userTask>`
            } else if (!getEdgeTargetId(node.id) && !getEdgeId(node.id)) {
                bpmn += `<bpmn:userTask id="n${node.id}">
                </bpmn:userTask>`
            } else if (getEdgeTargetId(node.id) && !getEdgeId(node.id)) {
                bpmn += `<bpmn:userTask id="n${node.id}">
                <bpmn:incoming>s${getEdgeTargetId(node.id)}</bpmn:incoming>
                </bpmn:userTask>`
            } else if (!getEdgeTargetId(node.id) && getEdgeId(node.id)) {
                bpmn += `<bpmn:userTask id="n${node.id}">
                <bpmn:outgoing>s${getEdgeId(node.id)}</bpmn:outgoing>
                </bpmn:userTask>`
            } else {
                bpmn += `<bpmn:userTask id="n${node.id}">
                <bpmn:incoming>s${getEdgeTargetId(node.id)}</bpmn:incoming>
                <bpmn:outgoing>s${getEdgeId(node.id)}</bpmn:outgoing>
                </bpmn:userTask>`
            }
        }
        //  else if (node.shape === 'custom-condition') {
        //     const conditionList = getConditionEdgeTargets(node.id)
        //     let str = ''
        //     for (let condition of conditionList) {
        //         str += `<bpmn:outgoing>s${condition.id}</bpmn:outgoing>`
        //     }
        //     bpmn += `<bpmn:exclusiveGateway id="n${node.id}">
        //             <bpmn:incoming>s${getEdgeTargetId(node.id)}</bpmn:incoming>
        //             ${str}
        //         </bpmn:exclusiveGateway>`
        // }
    }
    for (const edge of edges) {
        if (edge?.attrs?.condition?.value) {
            bpmn += `<bpmn:sequenceFlow id="s${edge.id}" name="${edge.labels[0]}" sourceRef="n${edge.target.cell}" targetRef="n${edge.target.cell}">
            <bpmn:conditionExpression xsi:type="bpmn:tFormalExpression">${JSON.stringify(edge?.attrs?.condition?.value)}</bpmn:conditionExpression>
            </bpmn:sequenceFlow>`
        } else {
            bpmn += `<bpmn:sequenceFlow id="s${edge.id}" sourceRef="n${edge.source.cell}" targetRef="n${edge.target.cell}" />`
        }
    }
    bpmn += `</bpmn:process>
        <bpmndi:BPMNDiagram id="BPMNDiagram_1">
            <bpmndi:BPMNPlane id="BPMNPlane_1" bpmnElement="Process_file_approve_1">`

    for (const cell of nodes) {
        bpmn += `<bpmndi:BPMNShape id="n${cell.id}_di" bpmnElement="n${cell.id}">
                <dc:Bounds x="${cell.position.x}" y="${cell.position.y}" width="${cell.size.width}" height="${cell.size.height}" />
            </bpmndi:BPMNShape>`
    }
    for (const cell of edges) {
        bpmn += `<bpmndi:BPMNEdge id="s${cell.id}_di" bpmnElement="s${cell.id}">
                <di:waypoint x="210" y="118" />
                <di:waypoint x="210" y="165" />
            </bpmndi:BPMNEdge>`
    }
    bpmn += `</bpmndi:BPMNPlane>
        </bpmndi:BPMNDiagram>
        </bpmn:definitions>`

    // 获取输入的边数组
    function getInputEdgeTargets(targetId) {
        const edges = graphData.cells.filter((m) => m.shape === 'edge')
        const source = edges.filter((m) => m.target.cell === targetId)
        return source
    }

    function getEdgeTargetId(targetId) {
        const edges = graphData.cells.filter((m) => m.shape === 'edge')
        const source = edges.filter((m) => m.target.cell === targetId)
        if (source.length === 0) return
        return source[0].id
    }
    // 获取 输出的边数组
    function getConditionEdgeTargets(sourceId) {
        const edges = graphData.cells.filter((m) => m.shape === 'edge')
        const source = edges.filter((m) => m.source.cell === sourceId)
        return source
    }

    function getEdgeId(sourceId) {
        const edges = graphData.cells.filter((m) => m.shape === 'edge')
        const source = edges.filter((m) => m.source.cell === sourceId)
        if (source.length === 0) return
        return source[0].id
    }

    return bpmn
}

