import { cloneDeep } from 'lodash'
export default function ({ dataIndex, formItem, title, model }) {
    const { type, opts } = formItem

    const componentAttr = cloneDeep(opts) || {}
    componentAttr.placeholder = componentAttr.placeholder || '请输入' + title

    if (['ElSelect', 'ElTreeSelect'].includes(type.name)) {
        componentAttr.placeholder = componentAttr.placeholder || '请选择' + title
        componentAttr.clearable = componentAttr.clearable ?? true
    }

    //给各事件传入查询区表单Model参数，利用引用对象特性，可实现字段间联动
    Object.keys(componentAttr).forEach((key) => {
        if (typeof componentAttr[key] === 'function') {
            componentAttr[key] = () => opts[key](model)
        }
    })

    const slots = componentAttr.slots
    delete componentAttr.slots

    return (
        <type {...componentAttr} modelValue={model[dataIndex]} onUpdate:modelValue={(val) => (model[dataIndex] = val)}>
            {slots}
        </type>
    )
}

import router from '../router'
export default {
    install(app) {
        app.directive('throttle', {
            mounted: (el, binding) => {
                let throttleTime = binding.value
                if (!throttleTime) {
                    throttleTime = 1000
                }
                let timer
                el.addEventListener(
                    'click',
                    (event) => {
                        if (!timer) {
                            timer = setTimeout(() => {
                                timer = null
                            }, throttleTime)
                        } else {
                            event && event.stopImmediatePropagation()
                        }
                    },
                    true,
                )
            },
        })

        app.directive('debounce', {
            mounted: (el, binding) => {
                let debounceTime = binding.value
                if (!debounceTime) {
                    debounceTime = 500
                }
                let timer
                el.addEventListener(
                    'click',
                    (event) => {
                        if (!timer) {
                            timer = setTimeout(() => {
                                timer = null
                            }, debounceTime)
                        } else {
                            event && event.stopImmediatePropagation()
                            clearTimeout(timer)
                            timer = setTimeout(() => {
                                timer = null
                            }, debounceTime)
                        }
                    },
                    true,
                )
            },
        })

        app.directive('has', {
            mounted: function (el, binding) {
                const { value } = binding
                if (typeof value !== 'string' || value === '') {
                    return
                }
                // 获取按钮权限
                const permissions: any[] = (router.currentRoute.value.meta.permissions as any[]) || []
                let permission
                for (let i = 0; i < permissions.length; i++) {
                    const item = permissions[i]
                    if (item.path === value) {
                        permission = item
                        break
                    }
                }
                if (!permission) {
                    el.style.display = 'none'
                }
            },
        })
        app.directive('disabled', {
            mounted: function (el, binding) {
                const { value } = binding
                // 获取按钮权限
                const permissions: any[] = (router.currentRoute.value.meta.permissions as any[]) || []
                let permission
                for (let i = 0; i < permissions.length; i++) {
                    const item = permissions[i]
                    if (item.path === value) {
                        permission = item
                        break
                    }
                }
                if (!permission) {
                    el.style.opacity = '0.6'
                    el.addEventListener(
                        'click',
                        (event) => {
                            event && event.stopImmediatePropagation()
                        },
                        true,
                    )
                }
            },
        })
    },
}

import { createI18n } from 'vue-i18n'
import { getBrowserLanguage } from '@/utils/general'

export const messages = {}

export default {
    install(app) {
        // 初始化时仅加载默认语言包
        const defaultLanguage = utils.getLoc('locale') || getBrowserLanguage() || 'zh-cn'
        const i18n = createI18n({
            locale: defaultLanguage,
            fallbackLocale: defaultLanguage,
            messages,
            globalInjection: true, // 允许全局使用$t
            fallbackWarn: false, //关闭找不到译文的告警提示
            missingWarn: false, //关闭找不到译文的告警提示
            missing: (locale, key, vm, result) => {
                const arr = key.split('.')

                //解决页面中依赖后端数据进行翻译的词条在接口未返回值时,页面会出现null的情况
                if (arr.at(-1) === 'null') {
                    return ''
                }
                if (arr.at(-1).startsWith('其他-')) {
                    return arr.at(-1)
                }
                return arr.join(' ')
            },
        })

        import(`@/locales/${defaultLanguage}.ts`).then((res) => {
            messages[defaultLanguage] = res.default
            //改变local值,可以触发$t方法更新翻译内容,解决页面文本翻译时,语言包还没加载完成,导致的翻译异常情况
            i18n.global.locale.value = ''
            i18n.global.locale.value = defaultLanguage
            document.title = i18n.global.t('common.projectName')
        })

        app.use(i18n)
    },
}

